from typing import Dict

class Solution:
    def findTheLongestSubstring(self, s: str) -> int:
        bit_mask = {
            'a': 0b10000,
            'e': 0b01000,
            'i': 0b00100,
            'o': 0b00010,
            'u': 0b00001
        }
        sum_dict = { 0 : -1}
        vowels = 0b00000
        max_len = 0
        for j, c in enumerate(s):
            if c in bit_mask:
                vowels ^= bit_mask[c]

            if vowels not in sum_dict:
                sum_dict[vowels] = j
            else:
                i = sum_dict[vowels]
                max_len = max(max_len, j - i)

        return max_len


def main():
    s = Solution()
    print(s.findTheLongestSubstring('eleetminicoworoep'))
    print(s.findTheLongestSubstring('leetcodeisgreat'))
    print(s.findTheLongestSubstring('bcbcbc'))
    print(s.findTheLongestSubstring(""))
    print(s.findTheLongestSubstring(""))

if __name__ == '__main__':
    main()